def find_duplicates(data):
    seen = set()
    duplicates = []

    for element in data:
        if element in seen:
            duplicates.append(element)
        else:
            seen.add(element)

    return duplicates

if __name__ == "__main__":
    arr = [3, 2, 1, 2, 3, 4]
    duplicates = find_duplicates(arr)
