def find_median(data):
    data.sort()
    n = len(data)

    if n % 2 == 0:  
        median = (data[n//2] + data[n//2-1])/2
    else: 
        median = data[n//2] 

    return median

if __name__ == "__main__":
    data = [10, 20, 30, 40]
    median = find_median(data)
    print("The median is:", median)

def check_prime(num): 

  # Negative ya zero input ke liye check kariye 
  if num <= 0: 
    return False

  # 1 ke liye check kariye  
  if num == 1: 
    return False

  # Check kariye ke number 2 aur n-1 ke beech kisi bhi number se divisible hai ya nahi  
  for i in range(2, num): 
    if num % i == 0: 
      return False

  # Agar koi divisor nahi hai, to number prime hai  
  return True

def remove_duplicates(sentence):
  word_list = sentence.split() # sentence ko words ki list mein split karo
  new_list = [] # nayi list jo unique words contain karegi

  # list mein har word ke liye loop chalaiye
  for word in word_list:
    if word not in new_list: # agar word unique hai to nayi list mein add karo
      new_list.append(word)

  # nayi list mein words ko join kar ke naya sentence banaiye
  return ' '.join(new_list)

