}

def printPrime(n): 
    # Tamam boolean array ke entries ko true ke tor par initialize kariye 
    prime = [True for i in range(n+1)] 
    p = 2
    while (p * p <= n): 
        if (prime[p] == True): 
            for i in range(p * p, n+1, p): 
                prime[i] = False
        p += 1
    # Tamam prime numbers print kariye 
    print("First",n,"Prime numbers:")
    for p in range(2, n): 
        if prime[p]:  
            print(p, end = ' ') 

