triple A, B, C, D, O;

A = (-1/sqrt(55),0,3*sqrt(6)/sqrt(55));
B = (sqrt(5/11), -sqrt(6/11), 0);
C = (sqrt(5/11), sqrt(6/11), 0);
D = (-1/sqrt(55),0,-3*sqrt(6)/sqrt(55));
O = (0,0,0);

draw(O--A,Arrow3(6));
draw(O--B,Arrow3(6));
