C = (sqrt(5/11), sqrt(6/11), 0);
D = (-1/sqrt(55),0,-3*sqrt(6)/sqrt(55));
O = (0,0,0);
M = (B + C)/2;

draw(O--A,dashed);
draw(O--B,dashed);
draw(O--C,dashed);
draw(O--D,dashed);
