def search(node, data): 

    # Base Case: agar root None hai ya key root par mojood hai 
    if node is None or node.data == data: 
        return node 

    # Key root ki key se badi hai 
    if node.data < data: 
        return search(node.right, data) 

    # Key root ki key se chhoti hai 
    return search(node.left, data)

def is_prime(n):
    if n <= 1:
        return False
    else:
        for i in range(2,n):
            if n % i == 0:
                return False
        return True

def find_max(lst):
