import random

def bubble_sort(lst):
    n = len(lst)
    for i in range(n):
        for j in range(0, n-i-1):
            if lst[j] > lst[j+1]:
                lst[j], lst[j+1] = lst[j+1], lst[j]
    return lst

def test_sort():
    lst = [random.randint(0, 100) for _ in range(1000)]
    sorted_lst = bubble_sort(lst)
    assert sorted_lst == sorted(lst), "Sorting failed"
    print("Sorting successful")

test_sort()

