def isAnagram(string1, string2): 

    # Yeh yaqini banayein ke dono strings ki length barabar hai 
    if len(string1) != len(string2): 
        return False

    # Dictionary jo string1 ke characters ki frequency store karegi 
    count = [0] * 26

    # Har character ke liye string1 mein uski frequency barhayein 
    for i in range(len(string1)): 
        count[ord(string1[i]) - ord('a')] += 1

    # Har character ke liye string2 mein uski frequency kam karein 
    for i in range(len(string2)): 
        count[ord(string2[i]) - ord('a')] -= 1

    # Agar koi frequency negative ho jaye, 
    # to strings anagram nahi ho sakte 
    for i in range(len(count)): 
        if count[i] < 0: 
            return False

    # Agar tamam frequencies zero hain, to string1 aur string2 
    # ek dosre ke anagram hain 
    return True

import socket

class Server:
    def __init__(self, host, port):
        self.host = host
        self.port = port
        self.socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.socket.bind((self.host, self.port))

    def start(self):
        self.socket.listen(1)
        while True:
            conn, addr = self.socket.accept()
            print(f"Connected by {addr}")
            while True:
                data = conn.recv(1024)
                if not data:
                    break
                conn.sendall(data)
            conn.close()

server = Server("localhost", 8000)
server.start()

