import random

def recommend_songs(preferences, artist=None, genre=None):
    # Retrieve songs based on user preferences and optional filters
    all_songs = ["Song A", "Song B", "Song C", "Song D", "Song E"]
    recommended_songs = []
    for song in all_songs:
