def solve_maze(maze, start, end):
    stack = [(start, [start])]

    while stack:
        (x, y), path = stack.pop()

        if (x, y) == end:
            return path

        for move in get_possible_moves(maze, (x, y)):
            if move not in path:
                stack.append((move, path + [move]))

    return None

